/* Colorful Moving Star Animation */
.banner-section::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 200%;
    height: 200%;
    background: transparent;
    z-index: 0;
    pointer-events: none;
    background: radial-gradient(circle, rgba(255, 255, 255, 0.15) 20%, transparent 80%),
                radial-gradient(circle, rgba(255, 200, 200, 0.5) 15%, transparent 70%),
                radial-gradient(circle, rgba(200, 255, 200, 0.5) 15%, transparent 70%),
                radial-gradient(circle, rgba(200, 200, 255, 0.5) 15%, transparent 70%);
    background-blend-mode: screen;
    animation: moveStars 60s linear infinite, twinkleStars 5s ease-in-out infinite;
}

@keyframes moveStars {
    from {
        transform: translate(0, 0);
    }
    to {
        transform: translate(-100%, -100%);
    }
}

@keyframes twinkleStars {
    0%, 100% {
        opacity: 1;
    }
    50% {
        opacity: 0.5;
    }
}
